4":"\u8fd0\u9001\u76f8\u5173","faq_5":"\u652f\u4ed8\u76f8\u5173","faq_6":"\u6c34\u8d28\u68c0\u6d4b\u76f8\u5173\u4ea7\u54c1","faq_7":"\u98df\u54c1\u5b89\u5168\u76f8\u5173\u4ea7\u54c1","faq_8":"\u751f\u6280\u836f\u5986\u76f8\u5173\u4ea7\u54c1","faq_9":"\u8bd5\u5242\u76f8\u5173\u4ea7\u54c1","brand":"\u54c1\u724c","feature_product":"\u4ea7\u54c1\u7279\u8272","product_description":"\u4ea7\u54c1\u4ecb\u7ecd","detection_step":"\u68c0\u6d4b\u6b65\u9aa4","test_item":"\u68c0\u6d4b\u9879\u76ee","Instrument_specifications":"\u4eea\u5668\u89c4\u683c","other_product":"\u5176\u4ed6\u76f8\u5173\u4ea7\u54c1","legal_reseller":"\u6388\u6743\u7ecf\u9500\u5546","reseller_banner_desc":"\u6b22\u8fce\u900f\u8fc7\u672c\u4e1a\u67e5\u627e\u79d1\u5c14\u5ba2\u4e8e\u5168\u7403\u5404\u5730\u7684\u6388\u6743\u7ecf\u9500\u5546","forget_password":"\u5fd8\u8bb0\u5bc6\u7801","forget_password_q":"\u5fd8\u8bb0\u5bc6\u7801\u4e86\u5417?","forget_password_remind":"\u8bf7\u8f93\u5165\u60a8\u6ce8\u518c\u65f6\u6240\u4f7f\u7528\u7684\u4fe1\u7bb1\uff0c\u6211\u4eec\u5c06\u4f1a\u53d1\u9001\u4e00\u5219\u91cd\u7f6e\u5bc6\u7801\u7684\u4fe1\u4ef6\u7ed9\u60a8\uff0c\u8c22\u8c22\u3002","not_validate":"\u767b\u5f55\u5fc5\u987b\u901a\u8fc7\u9a8c\u8bc1","send_valid_letter":"\u5bc4\u9001\u9a8c\u8bc1\u4fe1","company_tw":"\u53f0\u6e7e\u8461\u8404\u738b\u751f\u6280\u80a1\u4efd\u6709\u9650\u516c\u53f8","about_1":"\u53d1\u5c55\u5386\u7a0b","about_2":"\u5173\u4e8e\u521b\u529e\u4eba","about_3":"\u5168\u7403\u8ba4\u8bc1","about_4":"\u5168\u7403\u5e03\u5c40","about1":"&lt;p&gt;\u8461\u8404\u738b\u751f\u6280\u6210\u7acb\u4e8e1969\u5e74\uff0c\u5386\u7ecf\u4e86\u4e09\u6b21\u521b\u65b0\uff0c\u5305\u542b\uff1a\u6210\u7acb\u5f53\u5e74\u63a8\u51fa\u672c\u571f\u7b2c\u4e00\u652f\u80fd\u91cf\u996e\u6599\u300c\u5eb7\u8d1d\u7279\u53e3\u670d\u6db2\u300d\u5e2d\u5377\u5e02\u573a; 1991\u5e74\u6210\u7acb\u751f\u7269\u79d1\u6280\u7814\u7a76\u6240\uff0c\u8de8\u8db3\u4fdd\u5065\u98df\u54c1\u9886\u57df;2010\u5e74\u7684\u7ec4\u7ec7\u53d8\u9769\u53ca\u54c1\u724c\u518d\u9020\u3002\u591a\u5e74\u4ee5\u6765\uff0c\u9010\u6b65\u53d1\u5c55\u5c5e\u4e8e\u81ea\u5df1\u7684\u5fae\u7b11\u66f2\u7ebf\uff0c\u4e0d\u4f46\u62e5\u6709\u5168\u53f0\u6700\u5927\u7684\u9166\u9175\u4ea7\u80fd\u53ca\u9886\u5148\u6280\u672f\uff0c\u4e5f\u662fPIC\/S GMP\u836f\u5382\uff0c\u66f4\u62e5\u6709ISO22000\u53caISO17025\u56fd\u9645\u8d28\u91cf\u8ba4\u8bc1\u628a\u5173\u98df\u54c1\u5b89\u5168\u3002\u6700\u91cd\u8981\u7684\u662f\u6df1\u8015\u5df2\u4e45\u7684\u672c\u571f\u54c1\u724c\u4f18\u52bf\uff0c\u5546\u54c1\u904d\u53ca\u5168\u56fd\u5404\u5927\u901a\u8def\uff0c\u5982\u6b64\u4e00\u6761\u9f99\u7684\u4f18\u52bf\u7686\u8ba9\u540c\u4e1a\u96be\u4ee5\u5339\u654c\u3002\u300c\u79d1\u6280\u3001\u5065\u5eb7\u3001\u5e0c\u671b\u300d\u4e00\u76f4\u662f\u8461\u8404\u738b\u751f\u6280\u7684\u7cbe\u795e\u603b\u6307\u6807\uff0c\u6211\u4eec\u4e5f\u5c06\u4ee5\u6b64\u4e3a\u57fa\u7840\uff0c\u4ee5\u300c\u5065\u5eb7\u4e13\u5bb6\u3001\u7167\u987e\u5168\u5bb6\u300d\u4e3a\u4f7f\u547d\uff0c\u63d0\u4f9b\u793e\u4f1a\u5927\u4f17\u66f4\u4e30\u5bcc\u7684\u751f\u547d\uff0c\u5171\u540c\u8fce\u5411\u5145\u6ee1\u5e0c\u671b\u7684\u672a\u6765\u3002&lt;\/p&gt;","about2":"\u516c\u53f8\u7b80\u4ecb","about3":"\u5730\u7406\u4f4d\u7f6e","about4":"\u8054\u7cfb\u6211\u4eec","about5":"\u4e0a\u6d77\u8461\u8404\u738b\u4f01\u4e1a\u6709\u9650\u516c\u53f8","about6":"\u300c\u4e0a\u6d77\u8461\u8404\u738b\u4f01\u4e1a\u6709\u9650\u516c\u53f8\u300d\u4e3a\u8461\u8404\u738b\u751f\u6280\u80a1\u4efd\u6709\u9650\u516c\u53f8\u767e\u5206\u4e4b\u767e\u8f6c\u6295\u8d44\u4e4b\u5b50\u516c\u53f8\u30021994\u5e74\u6210\u7acb\u4e8e\u4e0a\u6d77\u5e02\u677e\u6c5f\u533a\uff0c1997\u5e74\u6b63\u5f0f\u8425\u8fd0\u3002\u4e0a\u6d77\u8461\u8404\u738b\u81ea\u6210\u7acb\u4ee5\u6765\uff0c\u4e0d\u4ec5\u5f00\u53d1\u3001\u62d3\u5c55\u81ea\u6709\u54c1\u724c\uff0c\u6301\u7eed\u7ed3\u5408\u6bcd\u516c\u53f8\u7684\u8d44\u6e90\u53ca\u6280\u672f\u80fd\u529b\uff0c\u4e5f\u79ef\u6781\u8fdb\u884c\u98df\u54c1\u3001\u4fdd\u5065\u54c1\u7684OEM\u548cODM\u4e1a\u52a1\u3002\u4e0d\u4f46\u53ef\u5b8c\u6574\u63d0\u4f9b\u7814\u53d1\u8bbe\u8ba1\u3001\u539f\u6599\u5236\u6210\u3001\u751f\u4ea7\u5de5\u7a0b\u3001\u8d28\u91cf\u63a7\u7ba1\u7684\u5404\u9879\u5ba2\u5236\u5316\u670d\u52a1\uff0c\u4e5f\u53ef\u4f9d\u5ba2\u6237\u9700\u6c42\u751f\u4ea7\u548c\u5f00\u53d1\u5dee\u5f02\u6027\u5546\u54c1\uff0c\u4f7f\u5ba2\u6237\u66f4\u62e5\u6709\u5e02\u573a\u4f18\u52bf\u53ca\u7ade\u4e89\u529b\u3002\u81ea2015\u5e74\u5f00\u59cb\u8fdb\u884c\u8425\u8fd0\u7b56\u7565\u7684\u8c03\u6574\uff0c\u5c06\u53d1\u5c55\u76ee\u6807\u8c03\u6574\u4ee5\u4ee3\u5de5\u4e1a\u52a1\u4e3a\u4e3b\uff0c\u4f7f\u5f972015\u5e74\u7684\u8425\u6536\u5927\u5e45\u6210\u957f72%\uff0c2017\u5e74\u603b\u8425\u6536\u66f4\u8d85\u8fc72014\u5e7411\u500d\uff0c\u4e00\u6761\u9f99\u5f0f\u7684\u4e13\u4e1a\u4ee3\u5de5\u670d\u52a1\uff0c\u4f7f\u4e0a\u6d77\u8461\u8404\u738b\u62e5\u6709\u591a\u5bb6\u4ee3\u5de5\u5ba2\u6237\uff0c\u5305\u542b\uff1a\u4fdd\u517b\u54c1\u724cTST\u5ead\u79d8\u5bc6\u3001\u5c1a\u8d6b\uff0c\u8dc3\u5347\u4e3a\u8461\u8404\u738b\u6700\u65b0\u7684\u6210\u957f\u5f15\u64ce\u3002","about7":"\u4e1c\u8461\u751f\u7269\u79d1\u6280\u6709\u9650\u516c\u53f8","about8":"\u4e1c\u8461\u751f\u7269\u79d1\u6280\u6709\u9650\u516c\u53f8\u6210\u7acb\u4e8e2017\u5e7410\u6708\uff0c\u516c\u53f8\u5ea7\u843d\u4e8e\u73af\u5883\u4f18\u7f8e\u7684\u4e1c\u839e\u677e\u5c71\u6e56\u7554\u3002\u4e3a\u8461\u8404\u738b\u751f\u6280\u80a1\u4efd\u6709\u9650\u516c\u53f8\u5168\u8d44\u7ecf\u8425\u7684\u5b50\u516c\u53f8\uff0c\u4e3b\u8981\u4ece\u4e8b\u4fdd\u5065\u98df\u54c1\u3001\u98df\u54c1\u9500\u552e\u3002&lt;br&gt;\u505a\u4e3a\u6bcd\u516c\u53f8\u8461\u8404\u738b\u751f\u6280\u4e8e\u5927\u9646\u5730\u533a\u6574\u4f53\u5e03\u5c40\u7684\u91cd\u8981\u67a2\u7ebd\uff0c\u5e76\u4e14\u4ee5\u7acb\u8db3\u534e\u5357\u533a\u653e\u773c\u5927\u9646\u5730\u533a\u5927\u5065\u5eb7\u4ea7\u4e1a\u7684\u613f\u666f\u4e0e\u76ee\u6807\u3002\u4e1c\u8461\u751f\u7269\u79d1\u6280(\u4e1c\u839e)\u6709\u9650\u516c\u53f8\u5ef6\u7eed\u7740\u6bcd\u516c\u53f8\u8461\u8404\u738b\u751f\u6280\u6240\u8d4b\u4e88\u7684\u300c\u79d1\u6280\u3001\u5065\u5eb7\u3001\u5e0c\u671b\u300d\u8fd9\u6837\u7684\u7cbe\u795e\u603b\u6307\u6807\uff0c\u505a\u4e3a\u516c\u53f8\u4e0d\u65ad\u52aa\u529b\u7684\u65b9\u5411\u3002\u5e76\u671f\u76fc\u4ee5\u6b64\u4e3a\u57fa\u7840\uff0c\u4ee5\u300c\u5065\u5eb7\u4e13\u5bb6\u3001\u7167\u987e\u5168\u5bb6\u300d\u4e3a\u4f7f\u547d\uff0c\u6765\u6ee1\u8db3\u5168\u5bb6\u4eba\u4fdd\u5065\u9700\u6c42\uff0c\u5e76\u521b\u9020\u66f4\u4e3a\u591a\u5143\u7684\u4fdd\u5065\u5951\u673a\u3002","about9":"\u8461\u4f17\u4f01\u4e1a\u80a1\u4efd\u6709\u9650\u516c\u53f8","about10":"\u8461\u4f17\u521b\u7acb\u5bfc\u56e0\u4e8e\u8461\u8404\u738b\u751f\u6280\u521b\u529e\u4eba\u66fe\u6c34\u7167 \u5148\u751f\u5bf9\u4e8e\u4fdd\u5065\u98df\u54c1\u98ce\u884c\u53ca\u591a\u5c42\u6b21\u4f20\u9500\u65f6\u4ee3\u6765\u4e34\u7684\u524d\u77bb\u8fdc\u89c1\uff0c\u4e8e1993\u5e74\u6210\u7acb\u4ee5\u4f20\u76f4\u9500\u4e3a\u4f53\u7cfb\u7684\u300c\u8461\u4f17\u4f01\u4e1a\u300d\u4e13\u653b\u4fdd\u5065\u98df\u54c1\u5e02\u573a\u3002\u627e\u6765\u516d\u5927\u987e\u95ee\uff0c\u5e76\u4e8e1998\u5e74\u8461\u4f17\u6b63\u5f0f\u8f6c\u578b\u4e3a\u591a\u5c42\u6b21\u4f20\u9500\u516c\u53f8\uff0c\u5960\u5b9a\u8461\u8404\u738b\u751f\u6280\u591a\u89d2\u5316\u7ecf\u8425\u7684\u91cd\u8981\u91cc\u7a0b\u7891\u3002\u300c\u8461\u4f17\u4f01\u4e1a\u300d\u76ee\u524d\u5171\u6709\u53f0\u5317\u3001\u6843\u56ed\u3001\u65b0\u7af9\u3001\u4e30\u539f\u3001\u53f0\u4e2d\u3001\u53f0\u5357\u3001\u9ad8\u96c4\u3001\u82b1\u83b2\u516b\u5904\u8425\u8fd0\u4e2d\u5fc3\u30022013\u5e74\u66f4\u65a5\u8d44\u8d2d\u7f6e\u53f0\u5317\u5185\u6e56\u5927\u697c\uff0c\u6253\u9020\u5168\u65b0\u8425\u8fd0\u603b\u90e8\u4ee5\u6ee1\u8db3\u4e0e\u65e5\u4ff1\u589e\u7684\u4f1a\u5458\u6570\uff0c\u63d0\u4f9b\u66f4\u4f18\u8d28\u5b8c\u5584\u7684\u670d\u52a1\u8d28\u91cf\u3002","about11":"\u4f01\u4e1a\u613f\u666f","about12":"1969\u5e74\u4ee5\u4f86\uff0c\u8461\u8404\u738b\u751f\u6280\u4e00\u76f4\u4e0d\u65b7\u81ea\u6211\u97ad\u7b56\u52aa